import React,{Component} from 'react';
import RecordFormComponentState from './record_form_component_state';
import MediaItem from './media_item';
import Dropzone from 'react-dropzone';
import {observer} from "mobx-react";

@observer class Media extends Component {
  constructor(props) {
    super(props);

    const data  = "data:image/jpeg;base64,";
    const type  = "image";
    const title = "title";
    const description = "description";
    const credit = "credit";

    const items = [
      {data: data, type: type, title: title, description: description, credit: credit}
    ];

    this.state = {is_visible: false, items: items};
  }

  onDrop(acceptedFiles, rejectedFiles) {
    acceptedFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = (f) => {
        const fileData = reader.result;

        let items = this.state.items.slice();
        items.push({data: fileData, type: f.type, title: f.filename, description: '', credit: ''})

        this.setState({items: items});
      };

      reader.fileName = file.name;
      reader.readAsDataURL(file);
    });
  }

  setCurrentMediaItem() {
    console.log("Set current");
  }

  render() {
    const pane_styles = {display: this.props.recordFormStore.visible_pane==='media' ? 'block' : 'none'};

    // const media_items = this.state.items.map((item,i) => {
    //   return <MediaItem {...item} {...this.props} key={i} />
    // });
    const media_items = [];

    return (
      <div className="section">
        <h2 className="title" onClick={()=>this.props.recordFormStore.visible_pane = 'media'}>Add media &amp; documents</h2>

        <div className="pane" style={pane_styles}>
          <div className="m-add-media-and-documents">

            <div className="thumbs">
              <Dropzone disableClick={true} onClick={()=>console.log("clicked")} activeStyle={{border: '4px solid red'}} accept="image/jpeg, image/png" onDrop={this.onDrop.bind(this)}>
                <ul>
                  {media_items}

                  <li className="add">
                    <a href="#"><span className="image"></span><em>Drag &amp; drop</em></a>
                  </li>
                </ul>

                <div className="add-by-url">
                  <div className="form-group">
                    <label>Add by URL (Youtube or SoundCloud URL)</label>
                    <input placeholder="URL" type="text" />
                  </div>
                  <button>Add</button>
                </div>
              </Dropzone>
            </div>

            <div className="meta">
              <div className="image random-image" style={{backgroundImage: 'url("/example/10.jpg")'}}></div>
              <div className="caption">
                <div className="form-group">
                  <label>Title</label>
                  <input placeholder="Title" type="text" />
                </div>
                <div className="form-group">
                  <label>Caption</label>
                  <textarea rows="5" placeholder="Caption"></textarea>
                </div>
                <div className="form-group">
                  <label>Credit</label>
                  <input placeholder="Credit" type="text" />
                </div>
                <div className="form-group">
                  <button>Set as primary image</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default RecordFormComponentState.bindComponent(Media);
